import React, { Component } from "react";
import uniqid from "uniqid";


class CvPractExpForm extends Component {
    constructor (props) {
      super(props);
      this.state = {   
        edArr: [],
        edExp: {
        companyName: "",
        positionTitle: "",
        dateWorking: "",
        id: uniqid(),
      }, 
        infoSubmited: false,
        showForm: false,
        showBtnPract: true } ;  

    }


    handleChangecompanyName = (e) => {
      this.setState({
        edExp: {
          companyName: e.target.value,
          positionTitle: this.state.edExp.positionTitle,
          dateWorking: this.state.edExp.dateWorking,
          id: this.state.edExp.id,


        }
      })
    }
    handleChangepositionTitle = (e) => {
      this.setState({
        edExp: {
          companyName: this.state.edExp.companyName,
          positionTitle: e.target.value,
          dateWorking: this.state.edExp.dateWorking,
          id: this.state.edExp.id,

        }
      })
    }
    handleChangedateWorking = (e) => {
      this.setState({
        edExp: {
          companyName: this.state.edExp.companyName,
          positionTitle: this.state.edExp.positionTitle,
          dateWorking: e.target.value,
          id: this.state.edExp.id,


        }
      })
    }
    handleChangId = (e) => {
      this.setState({
        edExp: {
          companyName: this.state.edExp.companyName,
          positionTitle: this.state.edExp.positionTitle,
          dateWorking: this.state.edExp.dateWorking,
          id: this.state.edExp.id,

        }
      })
    }
    handleChangeAge= (e) => {
      this.setState({
        edExp: {
          companyName: this.state.edExp.companyName,
          positionTitle: this.state.edExp.positionTitle,
          dateWorking: this.state.edExp.dateWorking,
          id: this.state.edExp.id,


        }
      })
    }

    onSubmitGenInfo = (e) => {
      e.preventDefault();
      this.setState({
        edArr: this.state.edArr.concat(this.state.edExp),
        edExp: {
          companyName: "",
          positionTitle: "",
          dateWorking: "",
          id: uniqid(),
        },
      });
      this.setState({
        showForm: false,
        infoSubmited: true,
      })



    }

    showForm = () => {

      return( 
      <div>
      <form onSubmit={this.onSubmitGenInfo}>
       <label htmlFor="companyNameInput">Company name:</label>
       <input 
       onChange={this.handleChangecompanyName}
       id="companyNameInput"
       type="text">
       </input>
       <label htmlFor="positionTitleInput">Position title :</label>
       <input 
       onChange={this.handleChangepositionTitle}
       id="positionTitleInput"
       type="text">
       </input>
       <label htmlFor="dateWorkingInput">Date working from/until :</label>
       <input 
       onChange={this.handleChangedateWorking}
       id="dateWorkingInput"
       type="text">
       </input>
       <button
       type="submit">Submit
       </button>
       </form>
       </div>
      )
    }

    handleChangetitleStudyEd = (e) => {
      let newEdArr = this.state.edArr;
      let selectedExp = newEdArr.filter(edExp => edExp.id === this.state.selectedEditId );
      let newTitleStudy = e.target.value;
      let indexSelectedEdit = newEdArr.findIndex(edExp => edExp.id === this.state.selectedEditId);
      newEdArr[indexSelectedEdit].titleStudy = newTitleStudy; 


      this.setState({
      edArr: newEdArr,
      })
      console.log(newEdArr)
    }


    handleChangecompanyNameEd = (e) => {
      let newEdArr = this.state.edArr;
      let selectedExp = newEdArr.filter(edExp => edExp.id === this.state.selectedEditId );
      let newCompanyName = e.target.value;
      let indexSelectedEdit = newEdArr.findIndex(edExp => edExp.id === this.state.selectedEditId);
      newEdArr[indexSelectedEdit].schoolName = newCompanyName; 


      this.setState({
      edArr: newEdArr,
      })

    }


    handleChangeDateWorkingEd = (e) => {
      let newEdArr = this.state.edArr;
      let selectedExp = newEdArr.filter(edExp => edExp.id === this.state.selectedEditId );
      let newDateWorking = e.target.value;
      let indexSelectedEdit = newEdArr.findIndex(edExp => edExp.id === this.state.selectedEditId);
      newEdArr[indexSelectedEdit].dateWorking = newDateWorking; 


      this.setState({
      edArr: newEdArr,
      })

    }

    onSubmitEditing() {

    }
    showEditFormEd() {
        let selectedExp = this.state.edArr.filter(edExp => edExp.id === this.state.selectedEditId );
        let newTitleStudy = selectedExp[0].titleStudy;
        let newSchoolName = selectedExp[0].schoolName;
        let newDateStudy = selectedExp[0].dateStudy
      return (
        <div>
        <form onSubmit={this.onSubmitEditing}>
         <label htmlFor="schoolNameInput">School name :</label>
         <input 
         onChange={this.handleChangeschoolNameEd}
         id="schoolNameInput"
         type="text"
         value={newSchoolName}>
         </input>
         <label htmlFor="titleStudyInput">Title of study :</label>
         <input 
         onChange={this.handleChangetitleStudyEd}
         id="titleStudyInput"
         type="text"
         defaultValue={newTitleStudy}>
         </input>
         <label htmlFor="dateStudyInput">Date of study :</label>
         <input 
         onChange={this.handleChangeDateStudyEd}
         id="dateStudyInput"
         type="text"
         value={newDateStudy}>
         </input>
         </form>
         </div>
      )
    }

    handleEdit = (e) => {
        this.setState({showEditFormEd:!this.state.showEditFormEd})
        this.setState({selectedEditId: e.target.id})

    }

    handleToggle() {
        this.setState({showForm:!this.state.showForm});
        this.setState({showBtnPract:!this.state.showBtnPract});

    }

    showBtnPract() {
         return ( <button onClick={() => this.handleToggle()}className="createPractExpBtn">+</button> )
    }

    render() {
      console.log(this.state)
      return (
        <div>
        {this.state.showBtnPract ? this.showBtnPract() : null }
        {this.state.showForm ? this.showForm() : null }
        <ul>
      {this.state.edArr.map((edExp) => {
        return <div> <p key={uniqid()}>{edExp.companyName}</p>
        <p key={uniqid()}> Title of Study : {edExp.positionTitle}</p>
        <p key={uniqid()}> Date of Study : {edExp.dateWorking}</p>
        <button id={edExp.id} onClick={this.handleEdit}>Edit</button>
        </div>;
      })}
    </ul>
         </div>

      );
    }


}

export default CvPractExpForm;
