import React, { Component } from "react";
import uniqid from "uniqid";

class CvEdExpForm extends Component {
    constructor (props) {
      super(props);

      this.state = {...this.state, 
        showForm: false} ;  

    }


    handleChangeFirstName = (e) => {
      this.setState({
        generalInfos: {
          firstName: e.target.value,

        }
      })
    }
    handleChangeLastName = (e) => {
      this.setState({
        generalInfos: {
          lastName: e.target.value,

        }
      })
    }
    handleChangeEmail = (e) => {
      this.setState({
        generalInfos: {
          email: e.target.value,

        }
      })
    }
    handleChangePhoneNumber = (e) => {
      this.setState({
        generalInfos: {
          phoneNumber: e.target.value,

        }
      })
    }
    handleChangeAge= (e) => {
      this.setState({
        generalInfos: {
          age: e.target.value,

        }
      })
    }

    onSubmitGenInfo = (e) => {
      e.preventDefault();
      this.setState({
        cvInfos: this.state.cvInfos.concat(this.state.task),
        generalInfos: {
          firstName: "",
          lastName: "",
          phoneNumber: "",
          email: "",
          age: "",
          id: uniqid(),
          }
      })
      console.log(this.state.generalInfos)
    }

    showForm = () => {
      return( 
      <div>
      <form onSubmit={this.onSubmitGenInfo}>
       <label htmlFor="firstNameInput">First Name :</label>
       <input 
       onChange={this.handleChangeFirstName}
       id="firstNameInput"
       type="text">
       </input>
       <label htmlFor="lastNameInput">Last Name :</label>
       <input 
       onChange={this.handleChangeLastName}
       id="lastNameInput"
       type="text">
       </input>
       <label htmlFor="emailInput">Email :</label>
       <input 
       onChange={this.handleChangeEmail}
       id="emailInput"
       type="email">
       </input>
       <label htmlFor="phoneInput">Phone Number:</label>
       <input
       onChange={this.handleChangePhoneNumber}
       id="phoneInput"
       type="number">
       </input>
       <label htmlFor="ageInput">Age :</label>
       <input
       onChange={this.handleChangeAge}
       id="ageInput"
       type="number">
       </input>
       <button
       type="submit">Submit
       </button>
       </form>
       </div>
      )
    }

    handleToggle() {
        this.setState({showForm:!this.state.showForm});
    }


    render() {
      return (
        <div>
        <button onClick={() => this.handleToggle()}className="createCvBtn">+</button>
        {this.state.showForm ? this.showForm() : null }
         </div>

      );
    }

  }

  export default CvEdExpForm;
