import React, { Component } from "react";
import uniqid from "uniqid";

class CvEdExpForm extends Component {
    constructor (props) {
      super(props);
      this.state = {showForm: false} ;  

    }


    handleChange = (e) => {
      this.setState()
    }

    onSubmitGenInfo = (e) => {
      e.preventDefault();
      this.setState({
        cvInfos: this.state.cvInfos.concat(this.state.generalInfos),
        generalInfos: {
          firstName: "",
          lastName: "",
          phoneNumber: "",
          email: "",
          age: "",
          id: uniqid(),
          }
      })
    }

    showForm = () => {
      return( 
      <div>
      <form onSubmit={this.onSubmitGenInfo}>
       <label htmlFor="firstNameInput">First Name :</label>
       <input 
       id="firstNameInput"
       type="text">
       </input>
       <label htmlFor="lastNameInput">Last Name :</label>
       <input 
       id="lastNameInput"
       type="text">
       </input>
       <label htmlFor="emailInput">Email :</label>
       <input 
       id="emailInput"
       type="email">
       </input>
       <label htmlFor="phoneInput">Phone Number:</label>
       <input
       id="phoneInput"
       type="number">
       </input>
       <label htmlFor="ageInput">Age :</label>
       <input
       id="ageInput"
       type="number">
       </input>
       </form>
       </div>
      )
    }

    handleToggle() {
        this.setState({showForm:!this.state.showForm});
    }


    render() {
      return (
        <div>
        <button onClick={() => this.handleToggle()}className="createCvBtn">+</button>
        {this.state.showForm ? this.showForm() : null }
         </div>
      );
    }

  }

  export default CvEdExpForm;
