import React, { Component } from "react";

class CvEdExp extends Component {
    constructor (props) {
      super(props);
      this.state = {showForm: false} ;  
      this.onClick = this.onClick.bind(this);
    }

    onClick () {
     this.setState({ showForm: true });
  }
    renderForm () {
      return( 
      <div>
      <form>
       <label htmlFor="firstNameInput">First Name :</label>
       <input 
       id="firstNameInput"
       type="text">
       </input>
       <label htmlFor="lastNameInput">Last Name :</label>
       <input 
       id="lastNameInput"
       type="text">
       </input>
       <label htmlFor="emailInput">Email :</label>
       <input 
       id="emailInput"
       type="email">
       </input>
       <label htmlFor="phoneInput">Phone Number:</label>
       <input
       id="phoneInput"
       type="number">
       </input>
       <label htmlFor="ageInput">Age :</label>
       <input
       id="ageInput"
       type="number">
       </input>
       </form>
       </div>
      )
    }


    render() {
      const { showForm } = this.state;
      return (
        <div>
        <button onClick={this.onClick } className="createCvBtn">+</button>
        </div>
      );
    }

  }

  export default CvEdExp;
