import React, { Component } from "react";
import uniqid from "uniqid";


class CvEdExpForm extends Component {
    constructor (props) {
      super(props);
      this.state = {  
        edArr: [], 
        edExp: {
        schoolName: "",
        titleStudy: "",
        dateStudy: "",
        id: uniqid(),
      }, 
        infoSubmited: false,
        showForm: false,
        showBtnEd: true,
        showEditFormEd: false, 
        selectedEditId: "",
      } 

      this.nameRef = React.createRef()

    }


    handleChangeschoolName = (e) => {
      this.setState({
        edExp: {
          schoolName: e.target.value,
          titleStudy: this.state.edExp.titleStudy,
          dateStudy: this.state.edExp.dateStudy,
          id: this.state.edExp.id,


        }
      })
    }
    handleChangetitleStudy = (e) => {
      this.setState({
        edExp: {
          schoolName: this.state.edExp.schoolName,
          titleStudy: e.target.value,
          dateStudy: this.state.edExp.dateStudy,
          id: this.state.edExp.id,

        }
      })
    }
    handleChangedateStudy = (e) => {
      this.setState({
        edExp: {
          schoolName: this.state.edExp.schoolName,
          titleStudy: this.state.edExp.titleStudy,
          dateStudy: e.target.value,
          id: this.state.edExp.id,


        }
      })
    }

    onSubmitGenInfo = (e) => {
      e.preventDefault();
      this.setState({
        edArr: this.state.edArr.concat(this.state.edExp),
        edExp: {
          schoolName: "",
          titleStudy: "",
          dateStudy: "",
          id: uniqid(),
        },
      });
      this.setState({
        showForm: false,
        infoSubmited: true,
      })


      this.setState({
        showBtnEd: !this.state.showBtnEd,
      })

    }





    showForm = () => {

      return( 
      <div>
      <form onSubmit={this.onSubmitGenInfo}>
       <label htmlFor="schoolNameInput">School name :</label>
       <input 
       onChange={this.handleChangeschoolName}
       id="schoolNameInput"
       type="text">
       </input>
       <label htmlFor="titleStudyInput">Title of study :</label>
       <input 
       onChange={this.handleChangetitleStudy}
       id="titleStudyInput"
       type="text">
       </input>
       <label htmlFor="dateStudyInput">Date of study :</label>
       <input 
       onChange={this.handleChangedateStudy}
       id="dateStudyInput"
       type="text">
       </input>
       <button
       type="submit">Submit
       </button>
       </form>
       </div>
      )
    }

    handleToggle() {
        this.setState({showForm:!this.state.showForm});
        this.setState({showBtnEd:!this.state.showBtnEd});

    }

    onSubmiteditInfo() {

    }

    handleChangetitleStudyEd = (e) => {
      let newEdArr = this.state.edArr;
      let selectedExp = this.state.edArr.filter(edExp => edExp.id === this.state.selectedEditId );
      let newTitleStudy = selectedExp[0].titleStudy;
     let indexSelectedEdit = this.state.edArr.findIndex(edExp => edExp.id === this.state.selectedEditId);
      newEdArr[indexSelectedEdit].titleStudy = newTitleStudy; 


      this.setState({
      edArr: newEdArr,
      })
      console.log(this.state.edArr)
    }

    showEditFormEd() {
       let selectedExp = this.state.edArr.filter(edExp => edExp.id === this.state.selectedEditId );
        let newTitleStudy = selectedExp[0].titleStudy;

      return (
        <div>
        <form>
         <label htmlFor="schoolNameInput">School name :</label>
         <input 
         onChange={this.handleChangeschoolName}
         id="schoolNameInput"
         type="text"
         value="lol">
         </input>
         <label htmlFor="titleStudyInput">Title of study :</label>
         <input 
         onChange={this.handleChangetitleStudyEd}
         id="titleStudyInput"
         type="text"
         value={newTitleStudy}>
         </input>
         <label htmlFor="dateStudyInput">Date of study :</label>
         <input 
         onChange={this.handleChangedateStudy}
         id="dateStudyInput"
         type="text"
         value="lol">
         </input>
         </form>
         </div>
      )
    }

    handleEdit = (e) => {
        this.setState({showEditFormEd:!this.state.showEditFormEd})
       this.setState({selectedEditId: e.target.id})


    }

    showBtnEd() {
         return ( <button onClick={() => this.handleToggle()}className="createEdExpBtn">+</button> )
    }


    render() {
      console.log(this.state)
      return (
        <div>
        {this.state.showBtnEd ? this.showBtnEd() : null }
        {this.state.showForm ? this.showForm() : null }
        {this.state.showEditFormEd ? this.showEditFormEd() : null}
        <ul>
      {this.state.edArr.map((edExp) => {
        return <div> <p key={uniqid()}>{edExp.schoolName}</p>
        <p key={uniqid()}> Title of Study : {edExp.titleStudy}</p>
        <p key={uniqid()}> Date of Study : {edExp.dateStudy}</p>
        <button id={edExp.id} onClick={this.handleEdit}>Edit</button>
        </div>;
      })}
    </ul>
         </div>

      );
    }


}

export default CvEdExpForm;
