import React, { Component } from "react";
import uniqid from "uniqid";


class CvPractExpForm extends Component {
    constructor (props) {
      super(props);
      this.state = {   
        edArr: [],
        edExp: {
        companyName: "",
        positionTitle: "",
        dateWorkingFrom: "",
        dateWorkingUntil: "",
        id: uniqid(),
      }, 
        infoSubmited: false,
        showForm: false,
        showBtnPract: true,
        showEditFormEd: false,
        selectedEditId: "",
      } ;  

    }


    handleChangecompanyName = (e) => {
      this.setState({
        edExp: {
          companyName: e.target.value,
          positionTitle: this.state.edExp.positionTitle,
          dateWorkingFrom: this.state.edExp.dateWorkingFrom,
          dateWorkingUntil: this.state.edExp.dateWorkingUntil,
          id: this.state.edExp.id,


        }
      })
    }
    handleChangepositionTitle = (e) => {
      this.setState({
        edExp: {
          companyName: this.state.edExp.companyName,
          positionTitle: e.target.value,
          dateWorkingFrom: this.state.edExp.dateWorkingFrom,
          dateWorkingUntil: this.state.edExp.dateWorkingUntil,
          id: this.state.edExp.id,

        }
      })
    }
    handleChangedateWorkingFrom = (e) => {
      this.setState({
        edExp: {
          companyName: this.state.edExp.companyName,
          positionTitle: this.state.edExp.positionTitle,
          dateWorkingFrom: e.target.value,
          dateWorkingUntil: this.state.edExp.dateWorkingUntil,
          id: this.state.edExp.id,


        }
      })
    }
    handleChangedateWorkingUntil = (e) => {
      this.setState({
        edExp: {
          companyName: this.state.edExp.companyName,
          positionTitle: this.state.edExp.positionTitle,
          dateWorkingFrom:  this.state.edExp.dateWorkingFrom,
          dateWorkingUntil: e.target.value,
          id: this.state.edExp.id,


        }
      })
    }


    onSubmitGenInfo = (e) => {
      e.preventDefault();
      this.setState({
        edArr: this.state.edArr.concat(this.state.edExp),
        edExp: {
          companyName: "",
          positionTitle: "",
          dateWorkingFrom: "",
          dateWorkingUntil:"",
          id: uniqid(),
        },
      });
      this.setState({
        showForm: false,
        infoSubmited: true,
      })
      this.setState({
        showBtnPract: !this.state.showBtnPract,
      })


    }

    showForm = () => {

      return( 
      <div>
      <form onSubmit={this.onSubmitGenInfo}>
       <label htmlFor="companyNameInput">Company name:</label>
       <input 
       onChange={this.handleChangecompanyName}
       id="companyNameInput"
       type="text">
       </input>
       <label htmlFor="positionTitleInput">Position title :</label>
       <input 
       onChange={this.handleChangepositionTitle}
       id="positionTitleInput"
       type="text">
       </input>
       <label htmlFor="dateWorkingInput">Date working from :</label>
       <input 
       onChange={this.handleChangedateWorkingFrom}
       id="dateWorkingInput"
       type="date"
       min="1980-01-01">
       </input>
       <label htmlFor="start">To :</label>
       <input 
       onChange={this.handleChangedateWorkingUntil}
       type="date" id="start" name="work-start"
       defaultValue=""
       min="1980-01-01"></input>
       <button
       type="submit">Submit
       </button>
       </form>
       </div>
      )
    }

    handleChangePositionTitleEd = (e) => {
      let newEdArr = this.state.edArr;
      let selectedExp = newEdArr.filter(edExp => edExp.id === this.state.selectedEditId );
      let newPositionTitle = e.target.value;
      let indexSelectedEdit = newEdArr.findIndex(edExp => edExp.id === this.state.selectedEditId);
      newEdArr[indexSelectedEdit].positionTitle = newPositionTitle; 


      this.setState({
      edArr: newEdArr,
      })
      console.log(newEdArr)
    }


    handleChangeCompanyNameEd = (e) => {
      let newEdArr = this.state.edArr;
      let selectedExp = newEdArr.filter(edExp => edExp.id === this.state.selectedEditId );
      let newCompanyName = e.target.value;
      let indexSelectedEdit = newEdArr.findIndex(edExp => edExp.id === this.state.selectedEditId);
      newEdArr[indexSelectedEdit].companyName = newCompanyName; 


      this.setState({
      edArr: newEdArr,
      })

    }


    handleChangeDateWorkingFromEd = (e) => {
      let newEdArr = this.state.edArr;
      let selectedExp = newEdArr.filter(edExp => edExp.id === this.state.selectedEditId );
      let newDateWorkingFrom = e.target.value;
      let indexSelectedEdit = newEdArr.findIndex(edExp => edExp.id === this.state.selectedEditId);
      newEdArr[indexSelectedEdit].dateWorkingFrom = newDateWorkingFrom; 


      this.setState({
      edArr: newEdArr,
      })

    }
    handleChangeDateWorkingUnilEd = (e) => {
      let newEdArr = this.state.edArr;
      let selectedExp = newEdArr.filter(edExp => edExp.id === this.state.selectedEditId );
      let newDateWorkingUntil = e.target.value;
      let indexSelectedEdit = newEdArr.findIndex(edExp => edExp.id === this.state.selectedEditId);
      newEdArr[indexSelectedEdit].dateWorkingUntil = newDateWorkingUntil; 


      this.setState({
      edArr: newEdArr,
      })

    }

    onSubmitEditing() {

    }
    showEditFormEd() {
        let selectedExp = this.state.edArr.filter(edExp => edExp.id === this.state.selectedEditId );
        let newPositionTitle = selectedExp[0].positionTitle;
        let newCompanyName = selectedExp[0].companyName;
        let newDateWorkingFrom = selectedExp[0].dateWorkingFrom
      return (
        <div>
        <form onSubmit={this.onSubmitEditing}>
         <label htmlFor="schoolNameInput">Company name :</label>
         <input 
         onChange={this.handleChangeCompanyNameEd}
         id="schoolNameInput"
         type="text"
         value={newCompanyName}>
         </input>
         <label htmlFor="titleStudyInput">Position title :</label>
         <input 
         onChange={this.handleChangePositionTitleEd}
         id="titleStudyInput"
         type="text"
         defaultValue={newPositionTitle}>
         </input>
         <label htmlFor="dateStudyInput">Date of working :</label>
         <input 
         onChange={this.handleChangeDateWorkingFromEd}
         id="dateStudyInput"
         type="text"
         value={newDateWorkingFrom}>
         </input>
         <input 
         onChange={this.handleChangeDateWorkingUntilEd}
         id="dateStudyInput"
         type="text"
         value={newDateWorkingUntil}>
         </input>
         </form>
         </div>
      )
    }

    handleEdit = (e) => {
        this.setState({showEditFormEd:!this.state.showEditFormEd})
        this.setState({selectedEditId: e.target.id})

    }

    handleToggle() {
        this.setState({showForm:!this.state.showForm});
        this.setState({showBtnPract:!this.state.showBtnPract});

    }

    showBtnPract() {
         return ( <button onClick={() => this.handleToggle()}className="createPractExpBtn">+</button> )
    }

    render() {
      console.log(this.state)
      return (
        <div>
        {this.state.showBtnPract ? this.showBtnPract() : null }
        {this.state.showForm ? this.showForm() : null }
        {this.state.showEditFormEd ? this.showEditFormEd() : null}
        <ul>
      {this.state.edArr.map((edExp) => {
        return <div> <p key={uniqid()}>{edExp.companyName}</p>
        <p key={uniqid()}> Position Title : {edExp.positionTitle}</p>
        <p key={uniqid()}> Date of Working from : {edExp.dateWorkingFrom}</p>
        <p key={uniqid()}> until : {edExp.dateWorkingUntil}</p>
        <button id={edExp.id} onClick={this.handleEdit}>Edit</button>
        </div>;
      })}
    </ul>
         </div>

      );
    }


}

export default CvPractExpForm;
