import React, { Component } from "react";
import './App.css';
import CvGenInfoForm from "./components/CvGenInfoForm.js";
import uniqid from "uniqid";
import CvPractExpForm from "./components/CvPractExpForm";
import CvEdExpForm from "./components/CvEdExpForm";

class App extends React.Component {


    state = {
      edExp: {
      schoolName: "",
      titleStudy: "",
      dateStudyFrom: "",
      dateStudyUntil: "",
      },
      edExpArr: [],

    }
    callbackFunction = (schoolName, titleStudy, dateStudyFrom, dateStudyUntil) => {
      this.setState({
      edExp: {  
      schoolName: schoolName,
      titleStudy: titleStudy,
      dateStudyFrom: dateStudyFrom,
      dateStudyUntil: dateStudyUntil,
      }
    })

      this.setState({
        edExpArr: this.state.edExpArr.concat(this.state.edExp),
      })

      this.setState({
        edExp: {
          schoolName: "",
          titleStudy: "",
          dateStudyFrom: "",
          dateStudyUntil: "",
          },
      })
    }

  render() {
    return (

      <div className="containerBase">
      <h1>CV Application</h1>
      <p className="titleSection">General Information</p>
      <CvGenInfoForm/>
      <p className="titleSection">Educational Experience</p>
      <CvEdExpForm parentCallback = {this.callbackFunction}/>
      <p className="titleSection">Practical Experience</p>
      <CvPractExpForm/>


      <div className="renderCv">
      <ul>
      {this.state.edExpArr.map((edExp) => {
        return <div className="divGen"> 
        <p>School Name : {edExp.schoolName}</p>
        <p>Title of Study : {edExp.titleStudy}</p>
        <p>From: {edExp.dateStudyFrom}</p>
        <p>Until : {edExp.dateStudyUntil}</p>
        </div>;
        })}
    </ul> 
       </div>
       </div>
    )

}

}
export default App;
