import React, { Component } from "react";
import './App.css';
import CvGenInfoForm from "./components/CvGenInfoForm.js";
import uniqid from "uniqid";
import CvPractExpForm from "./components/CvPractExpForm";
import CvEdExpForm from "./components/CvEdExpForm";

class App extends CvGenInfoForm {
  constructor (props) {
    super(props);

    this.state = {...this.state,
      cvInfos: [],

    }

  }


  overview() {
    <ul>
    {this.state.practArr.map((pract) => {
      return <div className="divGen"> 
      <p key={uniqid()}>Company Name : {pract.companyName}</p>
      <p key={uniqid()}>Position Title : {pract.positionTitle}</p>
      <p key={uniqid()}>Working from : {pract.dateWorkingFrom}</p>
      <p key={uniqid()}>until : {pract.dateWorkingUntil}</p>
      {this.state.showEditButton ? <button id={pract.id} className="buttonEdit" onClick={this.handleEdit}>Edit</button> : null}
     </div>;
    })}
  </ul>
  }
  render() {

    return (

      <div className="containerBase">
      <h1>CV Application</h1>
      <p className="titleSection">General Information</p>
      <CvGenInfoForm/>
      <p className="titleSection">Educational Experience</p>
      <CvEdExpForm/>
      <p className="titleSection">Practical Experience</p>
      <CvPractExpForm/> 
      </div>
    )

}

}
export default App;
