import React, { Component } from "react";

class CvEdExp extends Component {
    constructor (props) {
      super(props);
      this.state = {showForm: false} ;  

    }

    showForm = () => {
      return( 
      <div>
      <form>
       <label htmlFor="firstNameInput">First Name :</label>
       <input 
       id="firstNameInput"
       type="text">
       </input>
       <label htmlFor="lastNameInput">Last Name :</label>
       <input 
       id="lastNameInput"
       type="text">
       </input>
       <label htmlFor="emailInput">Email :</label>
       <input 
       id="emailInput"
       type="email">
       </input>
       <label htmlFor="phoneInput">Phone Number:</label>
       <input
       id="phoneInput"
       type="number">
       </input>
       <label htmlFor="ageInput">Age :</label>
       <input
       id="ageInput"
       type="number">
       </input>
       </form>
       </div>
      )
    }

    removeForm = () => {
        return(
            <div></div>
        )
    }


    render() {
      return (
        <div>
        <button onClick={function(){if(this.setState({showForm: true})){this.setState({showForm: false})} else {this.setState({showForm: true})}}} className="createCvBtn">+</button>
        {this.state.showForm ? this.showForm() : this.removeForm()}

        </div>
      );
    }

  }

  export default CvEdExp;
