import React, { Component } from "react";
import uniqid from "uniqid";


class CvGenInfoForm extends Component {
    constructor (props) {
      super(props);
      this.state = {   
        generalInfos: {
        firstName: "",
        lastName: "",
        phoneNumber: "",
        email: "",
        age: "",
        id: uniqid(),
      }, 
        infoSubmited: false,
        showForm: false,
        showBtn: true,
        showEditForm: false,
      } ;  

    }


    handleChangeFirstName = (e) => {
      this.setState({
        generalInfos: {
          firstName: e.target.value,
          lastName: this.state.generalInfos.lastName,
          phoneNumber: this.state.generalInfos.phoneNumber,
          email: this.state.generalInfos.email,
          age: this.state.generalInfos.age,
          id: this.state.generalInfos.id,


        }
      })
    }
    handleChangeLastName = (e) => {
      this.setState({
        generalInfos: {
          firstName: this.state.generalInfos.firstName,
          lastName: e.target.value,
          phoneNumber: this.state.generalInfos.phoneNumber,
          email: this.state.generalInfos.email,
          age: this.state.generalInfos.age,
          id: this.state.generalInfos.id,

        }
      })
    }
    handleChangeEmail = (e) => {
      this.setState({
        generalInfos: {
          firstName: this.state.generalInfos.firstName,
          lastName: this.state.generalInfos.lastName,
          phoneNumber: this.state.generalInfos.phoneNumber,
          email: e.target.value,
          age: this.state.generalInfos.age,
          id: this.state.generalInfos.id,


        }
      })
    }
    handleChangePhoneNumber = (e) => {
      this.setState({
        generalInfos: {
          firstName: this.state.generalInfos.firstName,
          lastName: this.state.generalInfos.lastName,
          phoneNumber: e.target.value,
          email: this.state.generalInfos.email,
          age: this.state.generalInfos.age,
          id: this.state.generalInfos.id,

        }
      })
    }
    handleChangeAge= (e) => {
      this.setState({
        generalInfos: {
          firstName: this.state.generalInfos.firstName,
          lastName: this.state.generalInfos.lastName,
          phoneNumber: this.state.generalInfos.phoneNumber,
          email: this.state.generalInfos.email,
          age: e.target.value,
          id: this.state.generalInfos.id,


        }
      })
    }

    onSubmitGenInfo = (e) => {
      e.preventDefault();
      this.setState({
        cvInfos: [this.state.generalInfos],

      })
      this.setState({
        showForm: false,
        infoSubmited: true,
      })

      this.showGenInf()

    }

    showGenInf = () => {
      return (
        <div>
        <p>{this.state.generalInfos.firstName}</p>
        <p>{this.state.generalInfos.lastName}</p>
        <p>{this.state.generalInfos.email}</p>
        <p>{this.state.generalInfos.phoneNumber}</p>
        <p>{this.state.generalInfos.age}</p>
        <button onClick={() => this.handleEdit()}>Edit</button>
        </div>
        )
    }

    showForm = () => {

      return( 
      <div>
      <form onSubmit={this.onSubmitGenInfo}>
       <label htmlFor="firstNameInput">First Name :</label>
       <input 
       onChange={this.handleChangeFirstName}
       id="firstNameInput"
       type="text">
       </input>
       <label htmlFor="lastNameInput">Last Name :</label>
       <input 
       onChange={this.handleChangeLastName}
       id="lastNameInput"
       type="text">
       </input>
       <label htmlFor="emailInput">Email :</label>
       <input 
       onChange={this.handleChangeEmail}
       id="emailInput"
       type="email">
       </input>
       <label htmlFor="phoneInput">Phone Number:</label>
       <input
       onChange={this.handleChangePhoneNumber}
       id="phoneInput"
       type="tel">
       </input>
       <label htmlFor="ageInput">Age :</label>
       <input
       onChange={this.handleChangeAge}
       id="ageInput"
       type="number"
       >
       </input>
       <button
       type="submit">Submit
       </button>
       </form>
       </div>
      )
    }

    handleToggle() {
        this.setState({showForm:!this.state.showForm});
        this.setState({showBtn:!this.state.showBtn})

    }

    showBtn () {
      return (<button onClick={() => this.handleToggle()}className="createCvBtn">+</button>)
    }

    editGenInfo() {
      return (
        <div>
           <form onSubmit={this.onSubmitGenInfo}>
       <label htmlFor="firstNameInput">First Name :</label>
       <input 
       onChange={this.handleChangeFirstName}
       id="firstNameInput"
       type="text"
       value={this.state.generalInfos.firstName}>
       </input>
       <label htmlFor="lastNameInput">Last Name :</label>
       <input 
       onChange={this.handleChangeLastName}
       id="lastNameInput"
       type="text"
       value={this.state.generalInfos.lastName}>
       </input>
       <label htmlFor="emailInput">Email :</label>
       <input 
       onChange={this.handleChangeEmail}
       id="emailInput"
       type="email"
       value={this.state.generalInfos.email}>
       </input>
       <label htmlFor="phoneInput">Phone Number:</label>
       <input
       onChange={this.handleChangePhoneNumber}
       id="phoneInput"
       type="tel"
       value={this.state.generalInfos.phoneNumber}>
       </input>
       <label htmlFor="ageInput">Age :</label>
       <input
       onChange={this.handleChangeAge}
       id="ageInput"
       type="number"
       value={this.state.generalInfos.age}>
       </input>
       <button>Finish edit</button>
       </form>
        </div>
      )

    }

    handleEdit() {
    this.setState({showEditForm:!this.state.showEditForm})
    }

    render() {
      console.log(this.state)
      return (
        <div>
        {this.state.showBtn ? this.showBtn() : null}
        {this.state.showForm ? this.showForm() : null }
        {this.state.infoSubmited && !this.state.showForm ? this.showGenInf() : null}
        {this.state.showEditForm ? this.editGenInfo(): null}
         </div>

      );
    }

  }

  export default CvGenInfoForm;
