import React, { Component } from "react";
import uniqid from "uniqid";


class CvEdExpForm extends Component {
    constructor (props) {
      super(props);
      this.state = {   
        edExp: {
        schoolName: "",
       titleStudy: "",
       dateStudy: "",
        id: uniqid(),
      }, 
        infoSubmited: false,
        showForm: false,
        showBtnEd: true } ;  

    }


    handleChangeschoolName = (e) => {
      this.setState({
        edExp: {
          schoolName: e.target.value,
         titleStudy: this.state.edExp.lastName,
         dateStudy: this.state.edExp.phoneNumber,
          id: this.state.edExp.id,


        }
      })
    }
    handleChangeLastName = (e) => {
      this.setState({
        edExp: {
          schoolName: this.state.edExp.schoolName,
         titleStudy: e.target.value,
         dateStudy: this.state.edExp.phoneNumber,
          id: this.state.edExp.id,

        }
      })
    }
    handleChangeEmail = (e) => {
      this.setState({
        edExp: {
          schoolName: this.state.edExp.schoolName,
         titleStudy: this.state.edExp.lastName,
         dateStudy: e.target.value,
          id: this.state.edExp.id,


        }
      })
    }
    handleChangePhoneNumber = (e) => {
      this.setState({
        edExp: {
          schoolName: this.state.edExp.schoolName,
         titleStudy: this.state.edExp.lastName,
         dateStudy: this.state.edExp.phoneNumber,
        id: this.state.edExp.id,

        }
      })
    }
    handleChangeAge= (e) => {
      this.setState({
        edExp: {
          schoolName: this.state.edExp.schoolName,
         titleStudy: this.state.edExp.lastName,
         dateStudy: this.state.edExp.phoneNumber,
       id: this.state.edExp.id,


        }
      })
    }

    onSubmitGenInfo = (e) => {
      e.preventDefault();
      this.setState({
        cvInfos: [this.state.edExp],

      })
      this.setState({
        showForm: false,
        infoSubmited: true,
      })

      this.showGenInf()

    }

    showGenInf = () => {
      return (
        <div>
        <p className="genInfosTitle">Educational Experience : </p>
        <p>{this.state.edExp.schoolName}</p>
        <p>{this.state.edExp.titleStudy}</p>
        <p>{this.state.edExp.dateStudy}</p>

        </div>
        )
    }

    showForm = () => {

      return( 
      <div>
      <form onSubmit={this.onSubmitGenInfo}>
       <label htmlFor="schoolNameInput">School name :</label>
       <input 
       onChange={this.handleChangeschoolName}
       id="schoolNameInput"
       type="text">
       </input>
       <label htmlFor="titleStudyInput">Title of study :</label>
       <input 
       onChange={this.handleChangeLastName}
       id="titleStudyInput"
       type="text">
       </input>
       <label htmlFor="dateStudyInput">Date of study :</label>
       <input 
       onChange={this.handleChangeEmail}
       id="dateStudyInput"
       type="text">
       </input>
       <button
       type="submit">Submit
       </button>
       </form>
       </div>
      )
    }

    handleToggle() {
        this.setState({showForm:!this.state.showForm});
        this.setState({showBtnEd:!this.state.showBtn});

    }

    showBtnEd() {
         return ( <button onClick={() => this.handleToggle()}className="createEdExpBtn">+</button> )
    }

    render() {
      console.log(this.state)
      return (
        <div>
        {this.state.showBtnEd ? this.showBtnEd() : null }
        {this.state.showForm ? this.showForm() : null }
        {this.state.infoSubmited && !this.state.showForm ? this.showGenInf() : null}

         </div>

      );
    }


}

export default CvEdExpForm;
