import React, { Component } from "react";
import './App.css';


class App extends Component {
  constructor () {
    super();
    this.state = {
      showForm: false
    }

  }

  showForm = () => {
    return( <form display="none">
     <label htmlFor="firstNameInput">First Name :</label>
     <input 
     id="firstNameInput"
     type="text">
     </input>
     <label htmlFor="lastNameInput">Last Name :</label>
     <input 
     id="lastNameInput"
     type="text">
     </input>
     <label htmlFor="emailInput">Email :</label>
     <input 
     id="emailInput"
     type="email">
     </input>
     <label htmlFor="phoneInput">Phone Number:</label>
     <input
     id="phoneInput"
     type="number">
     </input>
     <label htmlFor="ageInput">Age :</label>
     <input
     id="ageInput"
     type="number">
     </input>
     </form>
    )
  }


  render() {

    return (
      <div className="containerBase">
      <h1>CV Application</h1>
      <button onClick={() => this.setState({showForm: true}) } className="createCvBtn">+</button>
      </div>
    );
  }

}


export default App;
